mapper.delete:
delete from setmeal
where id in

    #{item}

mapper.select:
select s.id,s.name,s.category_id,s.price,s.image,s.description,s.status,s.update_time,c.name categoryName
from setmeal s join category c
on s.category_id=c.id


        and s.name like concat('%',#{name},'%')


        and s.status = #{status}


        and s.category_id = #{categoryId}
